  and  currentprice = #{currentprice,jdbcType=DOUBLE}


   and daypercent = #{daypercent,jdbcType=DOUBLE}


  and  percent = #{percent,jdbcType=DOUBLE}


  and  manager = #{manager,jdbcType=VARCHAR}


  and  customer like concat('%',#{customer,jdbcType=VARCHAR},'%')


  and  phone = #{phone,jdbcType=VARCHAR}


    and num = #{num,jdbcType=VARCHAR}


   and comment = #{comment,jdbcType=VARCHAR}




    and createtime between #{beginTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


    and createtime=#{beginTime,jdbcType=TIMESTAMP}
    and createtime=#{endTime,jdbcType=TIMESTAMP}


  and  zsprice = #{zsprice,jdbcType=VARCHAR}


